    AND ac.`cwdate` = #{emp.cwdate}


    AND ac.`username` = #{emp.username}

 SELECT * FROM `account`ac WHERE
1=1

     AND  ac.`goodsname` LIKE CONCAT('%',CONCAT(#{emp.goodsname},'%'))


